sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var ci = new  GlideRecord('cmdb_ci');

	if(current.u_type == "Scheduled"){
	ci.get(current.cmdb_ci.sys_id);
	ci.u_last_maintenance_completed = current.closed_at;
	ci.update();}

	var maint_data = new GlideRecord('u_maintenance_data');
	maint_data.addEncodedQuery('u_maintenance_order='+current.sys_id+'^u_parameter_evaluation=fail');
	maint_data.query();
	if(maint_data.hasNext()){
		while(maint_data.next()){
			var rec_maint_order = new GlideRecord('u_maintenance_orders'); 
			rec_maint_order.initialize();
			rec_maint_order.u_type = 'Reactive';
			//	Scheduled Maintenance Order "Affected CI" -> Reactive Maintenance Order "Affected CI"
			rec_maint_order.cmdb_ci = current.cmdb_ci;
			//	Scheduled Maintenance Order "Sys id" -> Reactive Maintenance Order "Parent"
			rec_maint_order.parent = current.sys_id;
			//	Text "This reactive maintenance order was created from Maintenance Order <Scheduled Maintenance Order "Number"> because the parameter check failed" <- Reactive Maintenance Order "Description of Issue"
			rec_maint_order.short_description = "This reactive maintenance order was created from Maintenance Order "+current.number+" because the parameter check failed";
			/*Description = Question:  <Maintenance order. Maintenance Data. Data Point. Question. Name>
			                Answer  :  <Maintenance order. Maintenance Data. Data Point. Answer. Name>" ...
			*/
			var tempStr = "Details on the failed parameter check are:\n Question: "+maint_data.u_data_point.u_questions.u_question.getDisplayValue()+"\n  Answer: "+maint_data.u_data_point.u_answer.u_answer+"\n User provided data ";
			tempStr += (maint_data.u_data_point.u_answer_type == 'choice' ? maint_data.u_choice_answer2.u_name :maint_data.u_number_answer);
			rec_maint_order.description = tempStr;
			rec_maint_order.insert();

		}
	}


})(current, previous);
